<?php	$interfaces = $GLOBALS['core']->query("SELECT * FROM interface WHERE (rootOrg={$_SESSION['OBJ_user']->rootOrg} OR rootOrg=0) AND module='{$GLOBALS['module']}' AND MOD_op='$operation' ORDER BY rootOrg, org, user_id ASC", true);	$intf = NULL;	while ($g = $interfaces->fetchRow()) {		if (!$intf) $intf = $g;		elseif ($intf['rootOrg'] == 0) {			if ($g['org'] == 0 || ($g['org'] == $_SESSION['OBJ_user']->org && ($g['user_id'] == $_SESSION['OBJ_user']->user_id || $g['user_id'] == 0)))				$intf = $g;		}		elseif ($intf['org'] == 0) {			if ($g['org'] == $_SESSION['OBJ_user']->org && ($g['user_id'] == $_SESSION['OBJ_user']->user_id || $g['user_id'] == 0))				$intf = $g;		}		elseif ($g['user_id'] == $_SESSION['OBJ_user']->user_id)			$intf = $g;	}	if ($intf) 		if ($GLOBALS['WXMODE'] && $intf['weixinFile']) $operation = $intf['weixinFile'];		elseif ($GLOBALS['APPMODE'] && $intf['appFile']) $operation = $intf['appFile'];		else $operation = $intf['file'];	else $GLOBALS['core']->sqlInsert(array('rootOrg'=>$_SESSION['OBJ_user']->rootOrg, 'module'=>$GLOBALS['module'], 'MOD_op'=>$operation, 'file'=>$operation), 'interface');
?>